package corea;

import corea.domain.Member;
import corea.domain.Participation;
import corea.domain.Room;
import corea.repository.MemberRepository;
import corea.repository.ParticipationRepository;
import corea.repository.RoomRepository;
import lombok.RequiredArgsConstructor;
import org.springframework.boot.CommandLineRunner;
import org.springframework.stereotype.Component;

import java.time.LocalDateTime;
import java.util.List;

@Component
@RequiredArgsConstructor
public class DataInitializer implements CommandLineRunner {
    private final MemberRepository memberRepository;
    private final RoomRepository roomRepository;
    private final ParticipationRepository participationRepository;

    @Override
    public void run(final String... args) throws Exception {
        final var members = memberRepository.saveAll(List.of(
                new Member("pororo@gmail.com", "뽀로로"),
                new Member("ashty@gmal.com", "애쉬"),
                new Member("moving@gmail.com", "무빙"),
                new Member("joyson@gmail.com", "조이썬썬"),
                new Member("tenten@gmail.com", "팔팔"),
                new Member("choco@gmail.com", "초코"),
                new Member("dal@gmail.com", "다르"),
                new Member("pobi@gmail.com", "포비")
        ));

        final Room room = new Room(
                "레이싱 카와 함께하는 TDD",
                "TDD를 배우고 싶은 자 나에게로",
                members.get(0).getId(),
                "https://github.com/jcoding-play/java-racingcar",
                "data:image/jpeg;base64,",
                3,
                "TDD",
                15,
                20,
                LocalDateTime.now()
                        .plusDays(7),
                LocalDateTime.now()
                        .plusDays(14)
        );
        roomRepository.save(room);

        participationRepository.saveAll(
                List.of(
                        new Participation(room.getId(), members.get(0).getId()),
                        new Participation(room.getId(), members.get(1).getId()),
                        new Participation(room.getId(), members.get(2).getId()),
                        new Participation(room.getId(), members.get(3).getId()),
                        new Participation(room.getId(), members.get(4).getId()),
                        new Participation(room.getId(), members.get(5).getId()),
                        new Participation(room.getId(), members.get(6).getId()),
                        new Participation(room.getId(), members.get(7).getId())

                ));
    }
}
